import { useMutation } from "@apollo/client"
import { ALL } from "../queries"
const Books = ({ show, books }) => {
  const [ search, result ] = useMutation(ALL) 
  const submit = (event) => {
    event.preventDefault()
    search({ genre: event.target.value })
    console.log(event.target.value, result)
  }
  if (!show) {
    return null
  }
  console.log(books)
  const genre = books.reduce((a,c) => a.genres !==  c.genres ?? a.genres)
  console.log('', genre)
  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books.map((a) => (
            <tr key={a.id}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {books.map((b) => (<button onClick={submit} value={b.genres} >{b.genres}</button>) )}
    </div>
  )
}

export default Books
