import { useQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL } from "../queries"

const Recommend = ({ show, data }) => {
    const [genre, setGenre] = useState(null)

    useEffect(() => {
        if (data) {
            setGenre(data.me.favouriteGenre)
        }
    }, [])

    const response = useQuery(ALL, {
        variables: { genre: data.me.favouriteGenre } 
    })

    console.log( data, 'response: ', response.data )
    if (!show) {
        return null
    }

    return (
        <div>
            <h1>recommendations</h1>
            <p>books in your favorite genre <b>{data?.me.favouriteGenre}</b></p>
        </div>
    )
}

export default Recommend
