const Books = ({ show, books, setGenre }) => {

  const submit = (event) => {
    event.preventDefault()
    const genre = event.target.value
    console.log(genre)
    setGenre(genre)
  }
  if (!show) {
    return null
  }
  console.log(books)
  const genre = books.map((b) => b.genres[0])
  const genres = genre.sort.reduce((a,c) => {
    return a !== c ?? c
  },[] )
  console.log('', genre, genres)
  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books.map((a) => (
            <tr key={a.id}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {books.map((b) => (<button onClick={submit} value={b.genres} >{b.genres}</button>) )}
      <button onClick={() => setGenre(null)}>reset filter</button>
    </div>
  )
}

export default Books
